import React, { useEffect, useState } from 'react';
import { obtenerUsuarios, eliminarUsuario } from '../api/usuarios';

const UsuariosList = () => {
  const [usuarios, setUsuarios] = useState([]);
  const [loading, setLoading] = useState(true);

  const cargarUsuarios = async () => {
    setLoading(true);
    const data = await obtenerUsuarios();
    setUsuarios(data);
    setLoading(false);
  };

  useEffect(() => {
    cargarUsuarios();
  }, []);

  const handleEliminar = async (id) => {
    if (window.confirm('¿Seguro que deseas eliminar este usuario?')) {
      const result = await eliminarUsuario(id);
      if (result.success) {
        cargarUsuarios(); // vuelve a cargar la lista
      } else {
        alert('Error al eliminar usuario');
      }
    }
  };

  if (loading) return <div>Cargando usuarios...</div>;

  return (
    <div className="max-w-2xl mx-auto mt-8 bg-white p-6 rounded-xl shadow">
      <h2 className="text-xl font-bold mb-4">Usuarios Registrados</h2>
      <table className="w-full border">
        <thead>
          <tr>
            <th>Usuario</th>
            <th>Correo</th>
            <th>Rol</th>
            <th>Fecha de creación</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {usuarios.map(u => (
            <tr key={u.Id}>
              <td>{u.NombreUsuario}</td>
              <td>{u.Correo}</td>
              <td>{u.Rol}</td>
              <td>{new Date(u.CreadoEn).toLocaleString()}</td>
              <td>
                <button
                  className="bg-red-500 text-white px-2 py-1 rounded"
                  onClick={() => handleEliminar(u.Id)}
                >
                  Eliminar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UsuariosList;
