import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import ClientServices from './pages/ClientServices';
import AdminDashboard from './pages/AdminDashboard';
import AdminProducts from './pages/AdminProducts';
import AdminBookings from './pages/AdminBookings';
import LoginModal from './components/LoginModal';
import { users } from './mock/users';
import Layout from './components/Layout'; // Asegúrate de importar Layout
import Register from './pages/Register';
import RestorePassword from './pages/RestorePassword';

const App = () => {
  const [currentUser, setCurrentUser] = useState(null);
  const [isLoginModalOpen, setIsLoginModalOpen] = useState(false);
  const navigate = useNavigate(); // Usamos useNavigate aquí

  const handleLogin = (username, password) => {
    return new Promise((resolve, reject) => {
      const userFound = users.find(
        (user) => user.username === username && user.password === password
      );
      if (userFound) {
        setCurrentUser(userFound);
        if (userFound.role === 'admin') {
          navigate('/admin');
        } else {
          navigate('/');
        }
        resolve();
      } else {
        reject(new Error('Usuario o contraseña incorrectos.'));
      }
    });
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('role');
    setCurrentUser(null); // Esto limpia el usuario y Layout se actualiza
    navigate('/');
  };

  const handleRegister = (form) => {
    // Crea solo usuarios estándar
    const newUser = {
      username: form.username,
      password: form.password,
      email: form.email,
      role: 'user'
    };
    users.push(newUser); // Si usas un mock, agrega aquí
    alert('Usuario registrado correctamente');
    navigate('/'); // Redirige al inicio o muestra el modal de login
  };

  const handleRestorePassword = (email) => {
    // Aquí deberías enviar el email al backend para restaurar la contraseña
    alert('Si el correo existe, recibirás instrucciones para restaurar tu contraseña.');
  };

  return (
    <>
      <Routes>
        <Route 
          path="/" 
          element={
            <Layout 
              user={currentUser} 
              onLogout={handleLogout} 
              onLoginClick={() => setIsLoginModalOpen(true)}
            >
              <ClientServices 
                user={currentUser} 
                onLoginClick={() => setIsLoginModalOpen(true)} // <-- AGREGA ESTA LÍNEA
              />
            </Layout>
          } 
        />
        <Route 
          path="/admin" 
          element={
            <Layout user={currentUser} onLogout={handleLogout} onLoginClick={() => setIsLoginModalOpen(true)}>
              <AdminDashboard user={currentUser} />
            </Layout>
          } 
        />
        <Route 
          path="/admin/products" 
          element={
            <Layout user={currentUser} onLogout={handleLogout}>
              <AdminProducts 
                user={currentUser} 
                onLoginClick={() => setIsLoginModalOpen(true)} 
              />
            </Layout>
          } 
        />
        <Route 
          path="/admin/bookings" 
          element={
            <Layout user={currentUser} onLogout={handleLogout}>
              <AdminBookings 
                user={currentUser} 
                onLoginClick={() => setIsLoginModalOpen(true)} 
              />
            </Layout>
          } 
        />
        <Route path="/register" element={<Register onRegister={handleRegister} />} />
        <Route path="/restore-password" element={<RestorePassword onRestore={handleRestorePassword} />} />
      </Routes>
      <LoginModal 
        isOpen={isLoginModalOpen} 
        onClose={() => setIsLoginModalOpen(false)} 
        onLogin={handleLogin} 
      />
    </>
  );
};

// Wrapper para usar useNavigate en App
const AppWrapper = () => (
  <Router>
    <App />
  </Router>
);

export default AppWrapper;
